import React, { Component } from 'react'
import MilestoneList from '../MilestoneList'
import CreateMilestoneForm from '../CreateMilestoneForm'

export default class MilestoneContainer extends Component {
	constructor(props) {
		super(props)
		this.state ={
			milestones:[]
		}
	}

	componentDidMount () {
		this.getMilestones()
	}


	getMilestones = async () => {
		try {
			const url = process.env.REACT_APP_API_URL + "/api/v1/milestones/" 
			const milestonesResponse = await fetch(url, {
				   	credentials: 'include',
				   	headers: { 
				   		'Content-Type': 'application/json' 
				   	}
				})	   
			const milestonesJson = await milestonesResponse.json()
			console.log('this is milestones json' ,milestonesJson);
			console.log(milestonesResponse);
			this.setState({
				milestones: milestonesJson.data
			})
		} catch (error) {
			console.log('Error getting Milestones data');
		}
	}

	createMilestone = async (milestoneToAdd) => {
		console.log('THIS SHOULD BE PROPS FROM APP');
 		console.log(this.props.userInfo)
 		console.log(this.props.userInfo.loggedInUserId);
 		console.log('HERE IS THE milestone BEING ADDED');
 		console.log(milestoneToAdd);
		try {
			const url = process.env.REACT_APP_API_URL + "/api/v1/milestones/" + this.props.courseId 
			const createdMilestoneResponse = await fetch(url, {
				method: 'POST',
				credentials: 'include',
				body: JSON.stringify(milestoneToAdd),
				headers: {
					'Content-Type':'application/json'
				}
			})
			console.log("createdMilsetoneResponse", createdMilestoneResponse);
			const createMilestoneJson = await createdMilestoneResponse.json()
			console.log('here is what we got back after trying to add a course');
			console.log(createMilestoneJson);
			if (createdMilestoneResponse.status === 201 ){
				this.setState({
					milestones:[...this.state.milestones, createMilestoneJson.data]
				})
			}
		} catch (error) {
			console.error("Error adding milestone");
			console.error(error)
		}

	}
	render() {
		console.log(' here is the state in render in Milestone COntainer');
		console.log(this.state);
		return (

			<React.Fragment>
				<h2>Milestone Container</h2>
				<CreateMilestoneForm />
				<MilestoneList 
					createMiletsone={this.createMiletsone} 
					milestones={this.state.milestones}/>
			</React.Fragment>


			)
	}
}
