import React from 'react'
import { Card, Button } from 'semantic-ui-react'





export default function CourseList(props) {
	console.log("props in courselidst");
	console.log(props);
	const course = props.courses.map(course => {
		return (
			<Card key={course.id} color={"purple"}>
				<Card.Content textAlign={'center'}>
					<Card.Header>
						{course.course_name}
					</Card.Header>
					<Card.Meta>
						Instructor: {course.administrator.username}	
					</Card.Meta>
					<Card.Meta>
						Milestones: {course.milestones.length}
					</Card.Meta>	
					{
						course.certification === true 
						&&
						<Card.Meta>
							Certification Upon Completion
						</Card.Meta>
					}

					<Card.Description>
						{course.description}
					</Card.Description>
					<Button 
						onClick={ () => props.createEnrollment(course.id)}
							>
						Enroll
					</Button> 
				{
					props.userInfo.loggedInUserId === course.administrator.id
					&&
					<React.Fragment>
						<Button 
							basic color='purple' 
							onClick={ () => props.editCourse(course.id)}>
							Course Editor
						</Button>
						<Button 
							className='negative mini ui button'
							onClick={ () => props.deleteCourse(course.id)}
						>
							Delete Course
						</Button>
					</React.Fragment>
				}
				</Card.Content>
			</Card>
		)
	})
	return (
		<Card.Group centered={true}>
			{course}
		</Card.Group>
	)
}
